let json = $json;

if (json.chatInput) {
  json.query = json.chatInput;
  json.kinDemandeur = "Un humain";
}

json.query = json.query.trim();

// Détecter la commande
let regexCommand = /\/crud\_mission\/([a-zA-Z0-9\_\-]+)/i;
json.command = "";
let matchCommand = json.query.match(regexCommand);
if (matchCommand) {
    json.command = matchCommand[1];
}

let regexFirstAttribute = /\/crud\_mission\/[a-zA-Z0-9\_\-]+\/([a-zA-Z0-9\_\-]+)/;
let matchFirstAttribute = json.query.match(regexFirstAttribute);
if (matchFirstAttribute) {
  json.firstAttribute = matchFirstAttribute[1];
}

let regexAllCommand = /\/\S+ /;
json.commandText = "";
let matchAllCommand = json.query.match(regexAllCommand);
if (matchAllCommand) {
  let startIndex = matchAllCommand[0].length;
  json.commandText = json.query.substring(startIndex);
}

return {"json": json};

