let thread = $node["entree"].json.thread;

function extractDynamicContent(thread) {
    let extractedContent = "";
    const contentPattern = /\[CONTENU[\s\S]*?][\s\S]*?\[\/CONTENU[\s\S]*?\]/gi;

    let matches;
    while ((matches = contentPattern.exec(thread)) !== null) {
        // Ajoute chaque contenu trouvé au texte final
        extractedContent += matches[0] + "\n\n";
    }

    return extractedContent.trim();
}

// Fonction pour retirer les occurrences
function removeOccurrences(document, pattern) {
    return document.replace(pattern, '');
}

// Garder le CONTENU
let document = extractDynamicContent(thread);

// Expression régulière pour identifier les occurrences à retirer
const pattern = /\n{0,2}\[\/?CONTENU.*?\]\n{0,2}/gi;

// Application de la fonction au document
document = removeOccurrences(document, pattern);

document = document.replace(/---/g, "\n");

if (document == "") {
    document = "[INFORMATIF - Pas de contenu]\n\nLe document est vide pour le moment (pas de baslises CONTENU trouvées dans le thread).\n\n[/NIFORMATIF - Pas de contenu]\n\n---\n\n" ;
}

return {"json":{"document":document}};

