const messages = $node["Get Thread"].json.messages;
let emailThread = "";
let remainingEmailThread = "";
const MAX_LENGTH = 10000;

messages.forEach(message => {
    let subject = 'n/a';
    let from = 'n/a';
    let to = 'n/a';

    if (message.payload && message.payload.headers) {
        const subjectHeader = message.payload.headers.find(header => header.name === 'Subject');
        const fromHeader = message.payload.headers.find(header => header.name === 'From');
        const toHeader = message.payload.headers.find(header => header.name === 'To');

        if (subjectHeader) subject = subjectHeader.value;
        if (fromHeader) from = fromHeader.value;
        if (toHeader) to = toHeader.value;
    }

    const date = new Date(parseInt(message.internalDate));
    const snippet = message.snippet;
    emailThread += `Sujet: ${subject}\nDe: ${from}\nÀ: ${to}\nDate: ${date.toLocaleString()}\nExtrait: ${snippet}\n\n`;
});


if (emailThread.length > MAX_LENGTH) {
    emailThread = "Voici la fin du thread d'email :\n[...]" + emailThread.substring(emailThread.length - MAX_LENGTH);
    remainingEmailThread = emailThread.substring(0, emailThread.length - MAX_LENGTH);
} else {
    remainingEmailThread = "";
}


return {"json" : {"emailThread": emailThread, "remainingEmailThread": remainingEmailThread}};

