let json = $json;

let query = "INSTRUCTION : Parmi les missions que tu as, renvoie le missionId de la mission la plus pertinente du "+ json.kin+" en te basant sur ces informations :

" + JSON.stringify($json) + "

Réponds toujours avec un missionId. REPONDS UNIQUEMENT avec le missionId, et rien d'autre (pas de guillemets, pas de "missionId :").

";


return {"json": {"query": query}};

let json = {};

json.query = "/crud_mission/read";

// Définir l'expression régulière
let regex = /kin[\w-]{2,25}[\w-]*/g;
let response = $json.response;

// Trouver toutes les correspondances
let matches = response.match(regex);

// Vérifier s'il y a des correspondances et assigner le dernier match
if (matches && matches.length > 0) {
    json.missionId = matches[matches.length - 1];
} else {
    // Gérer le cas où il n'y a pas de correspondances
    json.missionId = response; // ou toute autre valeur par défaut
}

return {"json": json};
