let json = {};

json.document = $item(0).$node["Convert thread to document"].json.document;

return {"json": json, "binary": $binary};

let binaries = {}, binary_keys = [];

for([index, item] of items.entries()) {
  if (item.binary) {
        binaries[Object.keys(item.binary)[0]+`_${index}`] = item.binary[Object.keys(item.binary)[0]];
    binary_keys.push(Object.keys(item.binary)[0] + `_${index}`);
  }
}

return [{
    json: {
        binary_keys: binary_keys.join(',')
    },
    binary: binaries
}];

let thread = $json.thread;

function extractDynamicContent(thread) {
  let extractedContent = "";
  const contentPattern = /^(\*\*|\[)([A-Z]{2,16}) - (.*?)(\*\*|\])([\s\S]*?)([-=]{3,4}|\[\/CONTENU - .*?\]|\Z)/gi;

  let matches;
  while ((matches = contentPattern.exec(thread)) !== null) {
      // Ajoute chaque contenu trouvé au texte final
      extractedContent += matches[5].trim() + "\n\n";
  }

  return extractedContent.trim();
}

// Garder le CONTENU
let document = extractDynamicContent(thread);

document = document.replace(/---/g, "\n");
document = document.replace(/===/g, "\n");

if (document == "") {
    document = "[INFORMATIF - Pas de contenu]\n\nLe document est vide pour le moment (pas de baslises CONTENU trouvées dans le thread).\n\n[/INFORMATIF - Pas de contenu]\n\n---\n\n" ;
}

return {"json":{"document":document}};

