document = removeOccurrences(document, pattern);

if (document == "") {
  document = "[EXCEPTION - Pas de contenu]\n\nPas de baslises DOCUMENT trouvées dans le thread !\n\n[/EXCEPTION - Pas de contenu]\n\n---\n\n\n[INFORMATIF - Thread]\n\n" + $json.thread + "[/INFORMATIF - Pas de contenu]\n\n";
}

return {"json":{"document":document}};

