function escapeMarkdownV2(text) {
    return text.replace(/[_*\[\]()~`>#\+=\-{}\.!|]/g, match => '\\' + match)
               .replace(/(\n```)([a-z]*\n[\s\S]+?\n```)/g, (match, p1, p2) => p1.replace('\n', '') + p2);
}

