function escapeMarkdownV2(text) {
    return text.replace(/[_*\[\]()~`>#\+=\-{}\.!|]/g, match => '\\' + match)
               .replace(/(\n```)([a-z]*\n[\s\S]+?\n```)/g, (match, p1, p2) => p1.replace('\n', '') + p2);
}

let originalText = '';
if ($json.output != null) {
  originalText = $json.output;
} else {
  originalText = $json.text;
}

const escapedText = escapeMarkdownV2(originalText);

return {"json":{"escapedText":escapedText}};

entireThread = $json.thread;
thread = $json.thread;
const MAX_LENGTH = 25000;

if (thread.length > MAX_LENGTH) {
    thread = "[...]" + thread.substring(thread.length - MAX_LENGTH);
    remainingThread = entireThread.substring(0, entireThread.length - MAX_LENGTH);
} else {
    remainingThread = ""; // Aucun contenu restant si la longueur du fil est inférieure à MAX_LENGTH
}

let sortie = "";

if ($json.sortie != "") {
  sortie = $json.sortie;
} else {
  sortie = $node["mission"].json.thread;
}

return {"sortie": sortie, "thread": thread, "entireThread": entireThread, "remainingThread": remainingThread};

let sortie = '';

// Ajouter l'output sans guillemets de début et de fin
if ($json.output != null) {
  sortie += '\n\n' + $json.output.replace(/^(["`]+)|(["`]+)$/g, ''); 
} else {
  sortie += '\n\n' + $json.text.replace(/^(["`]+)|(["`]+)$/g, ''); 
}


// Corriger le \n
sortie = sortie.replace('\n', '\r\n');

let thread = $node["thread"].json.entireThread.replace(/^(["`]+)|(["`]+)$/g, '');

// Enlever toutes les lignes qui contiennent les instructions
thread = thread.replace(/.*Prochaine étape.*$/gmi, '');
thread = sortie.replace(/.*Étape suivante.*$/gmi, '');
thread = thread.replace(/^.*niveau de boucle.*$/gmi, '');


// Ajouter l'output sans guillemets de début et de fin
if ($json.output != null) {
  thread += '\n\n' + $json.output.replace(/^(["`]+)|(["`]+)$/g, ''); 
} else {
  thread += '\n\n' + $json.text.replace(/^(["`]+)|(["`]+)$/g, ''); 
}

// Corriger le \n
thread = thread.replace('\n', '\r\n');

return {"sortie":sortie, "thread":thread};

