return {"json": {"promptProducteur": output}};

let json = $node["entree"].json;

if ($json.output != null) {
  json.output = $json.output;
} else {
  json.output = "[EXCEPTION - Error with OpenAI call]\n\n" + $prevNode.name +" returned an error when calling OpenAI Assistant\n\n[/EXCEPTION - Error with OpenAI call]"; 
}

return {"json": json};
