function truncateStringsInJson(obj) {
    // Vérifier si l'objet est null ou undefined
    if (obj === null || obj === undefined) {
        return obj;
    }

    // Parcourir les propriétés de l'objet
    for (let key in obj) {
        if (typeof obj[key] === 'string' && key !== "json") {
            // Tronquer les strings à 500 caractères et ajouter "[...]" si nécessaire
            if (obj[key].length > 5000) {
                obj[key] = obj[key].substring(0, 5000) + "[...]";
            }
        } else if (typeof obj[key] === 'object') {
            // Pas d'objets possible en metadata
            obj[key] = JSON.stringify(obj[key]).substring(0, 5000);

            if (obj[key].length >= 5000) {
              obj[key] += "[...]";
            }
        }
    }

    return obj;
}

// Function to remove null attributes from a JSON object
function removeNullAttributes(obj) {
    Object.keys(obj).forEach(key => {
        if (obj[key] === null || obj[key] == "") {
            delete obj[key];
        } else if (typeof obj[key] === 'object') {
            // Recursive call for nested objects
            removeNullAttributes(obj[key]);
        }
    });
}

