let json = $json;

if (!json.missionDemandeur) {
  json.missionDemandeur = "Compréhension générale & amélioration";
}

if (json.chatInput) {
  json.query = json.chatInput;
  json.kinDemandeur = "Un humain";
} else {
  json.kinDemandeur = json.kin;
}

if (json.kin == null) {
  json.kin = "kinmatcheur";
}

if (json.query) {
  // Trouver le bon Kin
  if (json.query.match(/kin{2,16}\w+/i)) {
    json.kin = json.query.match(/kin\w{2,16}/i)[0];
  } else if (json.query.match(/kin \w{2,16}/i)) {
    json.kin = json.query.match(/kin \w{2,16}/i)[0].replace(" ", "");
  }
} else {
  json.query =  "Je souhaite une explication du contenu du prompt de ce Kin.";
}

return {"json": json};

let assistantId;
const kinObject = $json.kin.find(kin => kin.kin === $node["entree"].json.kin.toLowerCase());

assistantId = kinObject && kinObject.assistantId ? kinObject.assistantId : "asst_8umriZllMPt14U8qy2T0jCmQ";

return {"assistantId": assistantId};
