let json = $json;

if (!json.missionDemandeur) {
  json.missionDemandeur = "Compréhension générale & amélioration";
}

if (json.chatInput) {
  json.query = json.chatInput;
  json.kinDemandeur = "Un humain";
} else {
  json.kinDemandeur = json.kin;
}

if (json.query) {
  // Trouver le bon Kin
  if (json.query.match(/kin\w+/i)) {
    json.kin = json.query.match(/kin\w+/i)[0];
  }
} else {
  json.query = "Je souhaite un résumé général de l'exécution.";
}

// Execution Status
if (json.query.match(/status: \w+/i)) {
  json.status = json.query.match(/status: \w+/i)[0].substring(8);
} else {
  json.status = "error";
}

// Workflow 
if (json.query.match(/workflowId: \w+/i)) {
  json.workflowId = json.query.match(/workflowId :\w+/i)[0].substring(12);
} else {
  json.workflowId = null;
}

return {"json": json};

