let document = $json.document;

// Enlever toutes les lignes qui contiennent les instructions
document = document.replace(/.*Prochaine étape.*$/gmi, '');
document = document.replace(/.*Étape suivante.*$/gmi, '');
document = document.replace(/^.*niveau de boucle.*$/gmi, '');
document = document.replace(/```.*```/gmi, '');
document = document.replace(/``.*``/gmi, '');
document = document.replace(/^[\r\n]+|[\r\n]+$/g, '');

return {"json":{"document":document}};

let entireThread = $json.thread;
let thread = $json.thread;
let instructions = $node["Execute Workflow Trigger"].json.query?$node["Execute Workflow Trigger"].json.query:$node["Execute Workflow Trigger"].json.instructions;
const MAX_LENGTH = 15000;

thread = thread.replace(/.*Prochaine étape.*$/gmi, '');
thread = thread.replace(/.*Étape suivante.*$/gmi, '');
thread = thread.replace(/^.*niveau de boucle.*$/gmi, '');
thread = thread.replace(/```.*```/gmi, '');
thread = thread.replace(/``.*``/gmi, '');
thread = thread.replace(/^[\r\n]+|[\r\n]+$/g, '');

if (thread.length > MAX_LENGTH) {
    thread = "Le début du document est déjà rédigé, voici les denières parties rédigées :\n[...]" + thread.substring(thread.length - MAX_LENGTH);
    remainingThread = entireThread.substring(0, entireThread.length - MAX_LENGTH);
} else {
    remainingThread = ""; // Aucun contenu restant si la longueur du fil est inférieure à MAX_LENGTH
}

return {"thread": thread, "entireThread": entireThread, "remainingThread": remainingThread, "instructions": instructions};

let output = $node["kinmail"].json.output;

// Enlever toutes les lignes qui contiennent les instructions
output = output.replace(/.*Prochaine étape.*$/gmi, '');
output = output.replace(/.*Étape suivante.*$/gmi, '');
output = output.replace(/^.*niveau de boucle.*$/gmi, '');
output = output.replace(/```.*```/gmi, '');
output = output.replace(/``.*``/gmi, '');
output = output.replace(/^[\r\n]+|[\r\n]+$/g, '');

return {"output": output};
