return {"json":{"text":text}};

let discordMessage =  $node["entree"].json.kin + " (Output Producteur) (Execution #" + $execution.id + ") - " + $node["entree"].json.mailSubject + ": \n\n" + $node["entree"].json.output + "\n\n---\n\n" + $node["entree"].json.kin + " (Nouvelles Instructions) (Execution #" + $node["entree"].json.executionId + ") - " + $node["entree"].json.mailSubject + ": \n\n" + $node["entree"].json.instructions;

let chunks = [];

// Taille maximale de chaque morceau
const chunkSize = 1990;

for (let i = 0; i < discordMessage.length; i += chunkSize) {
