let discordMessage =  $node["mission"].json.kin + " (Output Producteur) (Execution #" + $execution.id + ") - " + $node["mission"].json.mailSubject + ": \n\n" + $node["mission"].json.output + "\n\n---\n\n" + $node["mission"].json.kin + " (Nouvelles Instructions) (Execution #" + $node["entree"].json.executionId + ") - " + $node["mission"].json.mailSubject + ": \n\n" + $node["mission"].json.instructions;

let chunks = [];

// Taille maximale de chaque morceau
const chunkSize = 1990;

for (let i = 0; i < discordMessage.length; i += chunkSize) {
    // Découper le message en sous-chaînes de 1990 caractères
    const chunk = discordMessage.substring(i, i + chunkSize);
    chunks.push(chunk);
}

return {"json": {"chunks": chunks}};

