let discordMessage = "Output de " + $node["entree"].json.kin + " (Producteur) : " + $node["entree"].json.mailSubject + "\n\n" + $node["entree"].json.output + "\n\n---\n\n" + "Nouvelles Instructions de " + $node["entree"].json.kin + " (Manager) : " + $node["entree"].json.mailSubject + "\n\n" + $node["entree"].json.instructions;

let chunks = [];

// Taille maximale de chaque morceau
const chunkSize = 1990;

for (let i = 0; i < discordMessage.length; i += chunkSize) {
    // Découper le message en sous-chaînes de 1990 caractères
    const chunk = discordMessage.substring(i, i + chunkSize);
    chunks.push(chunk);
}

return {"json": {"chunks": chunks}};
