function extractDynamicContent(jsonDocument) {
    let extractedContent = "";
    const contentPattern = /\[CONTENU - [^\]]+ \](.*?)\[\/CONTENU - [^\]]+ \]/gs;

    let matches;
    while ((matches = contentPattern.exec(jsonDocument)) !== null) {
        // Ajoute chaque contenu trouvé au texte final
        extractedContent += matches[1] + "\n";
    }

    return extractedContent.trim();
}

