// Function to remove null attributes from a JSON object
function removeNullAttributes(obj) {
    Object.keys(obj).forEach(key => {
        if (obj[key] === null || obj[key] == "") {
            delete obj[key];
        } else if (typeof obj[key] === 'object') {
            // Recursive call for nested objects
            removeNullAttributes(obj[key]);
        }
    });
}

// Function to remove threads
function removePropertiesWithThread(obj) {
    for (let key in obj) {
        if (key.toLowerCase().includes('thread')) {
            // Supprime la propriété si la clé contient 'thread'
            delete obj[key];
        } else if (typeof obj[key] === 'object' && obj[key] !== null) {
            // Appel récursif si la propriété est un objet
            removePropertiesWithThread(obj[key]);
        }
    }
}

